// Separar los datos del dashboard en diferentes mensajes
const data = msg.payload;

// Datos actuales
msg.payload = {
    energy_price: data.current?.energy_price || 0,
    temperature: data.current?.temperature || 0,
    humidity: data.current?.humidity || 0,
    production_status: data.current?.production_status || "üîÑ Cargando..."
};
msg.topic = "current_data";
node.send([msg, null, null, null]);

// Predicciones ML
