node.send([null, predMsg, null, null]);

// Estado del sistema
const statusMsg = {...msg};
statusMsg.payload = {
    status: data.status || "üîÑ Cargando...",
    alerts_count: data.alerts_count || 0,
    timestamp: data.timestamp
};
