node.send([null, null, statusMsg, null]);

// Log para debug
const logMsg = {...msg};
logMsg.payload = `Dashboard actualizado: ${new Date().toLocaleTimeString()}`;
node.send([null, null, null, logMsg]);

return null;

const data = msg.payload;

// Formatear informaci√≥n detallada
let detailedInfo = "=== üè≠ DASHBOARD COMPLETO TFM CHOCOLATE FACTORY ===\n\n";

// Informaci√≥n actual
if (data.current_info) {
    detailedInfo += "üìä INFORMACI√ìN ACTUAL:\n";
    if (data.current_info.energy) {
        detailedInfo += `üí∞ Precio Energ√≠a: ${data.current_info.energy.price_eur_kwh} ‚Ç¨/kWh\n`;
        detailedInfo += `üìà Tendencia: ${data.current_info.energy.trend || 'estable'}\n`;
    }
    if (data.current_info.weather) {
        detailedInfo += `üå°Ô∏è Temperatura: ${data.current_info.weather.temperature}¬∞C\n`;
        detailedInfo += `üíß Humedad: ${data.current_info.weather.humidity}%\n`;
        detailedInfo += `üéØ Confort: ${data.current_info.weather.comfort_index || 'N/A'}\n`;
    }
    detailedInfo += `üè≠ Producci√≥n: ${data.current_info.production_status}\n\n`;
}

// Predicciones ML
if (data.predictions) {
    detailedInfo += "ü§ñ PREDICCIONES ML:\n";
    if (data.predictions.energy_optimization) {
        detailedInfo += `‚ö° Score Energ√≠a: ${data.predictions.energy_optimization.score}/100\n`;
        detailedInfo += `üí° Recomendaci√≥n: ${data.predictions.energy_optimization.recommendation}\n`;
    }
    if (data.predictions.production_recommendation) {
        detailedInfo += `üéØ Clase Producci√≥n: ${data.predictions.production_recommendation.class}\n`;
        detailedInfo += `üîß Acci√≥n: ${data.predictions.production_recommendation.action}\n`;
    }
    detailedInfo += "\n";
}

// Recomendaciones
if (data.recommendations) {
    detailedInfo += "üí° RECOMENDACIONES OPERATIVAS:\n";
    Object.keys(data.recommendations).forEach(category => {
        if (Array.isArray(data.recommendations[category]) && data.recommendations[category].length > 0) {
            detailedInfo += `${category.toUpperCase()}:\n`;
            data.recommendations[category].forEach(rec => {
                detailedInfo += `  ‚Ä¢ ${rec}\n`;
            });
        }
    });
    detailedInfo += "\n";
}

// Alertas
if (data.alerts && data.alerts.length > 0) {
    detailedInfo += "üö® ALERTAS ACTIVAS:\n";
    data.alerts.forEach((alert, index) => {
        detailedInfo += `${index + 1}. [${alert.level?.toUpperCase()}] ${alert.message}\n`;
        if (alert.action) {
            detailedInfo += `   Acci√≥n: ${alert.action}\n`;
        }
    });
    detailedInfo += "\n";
}

// Estado del sistema
if (data.system_status) {
    detailedInfo += "‚öôÔ∏è ESTADO DEL SISTEMA:\n";
    detailedInfo += `Estado: ${data.system_status.status}\n`;
    if (data.system_status.data_sources) {
        detailedInfo += "Fuentes de Datos:\n";
        Object.keys(data.system_status.data_sources).forEach(source => {
            detailedInfo += `  ‚Ä¢ ${source}: ${data.system_status.data_sources[source]}\n`;
        });
    }
}

detailedInfo += `\n‚è∞ √öltima actualizaci√≥n: ${new Date(data.timestamp).toLocaleString()}`;

msg.payload = detailedInfo;
return msg;

const data = msg.payload;
let alertText = "üö® ALERTAS ACTIVAS DEL SISTEMA\n\n";

if (data.alerts && data.alerts.length > 0) {
    data.alerts.forEach((alert, index) => {
        const levelEmoji = {
            'critical': 'üî¥',
            'warning': 'üü°', 
            'high': 'üü†',
            'info': 'üîµ'
        }[alert.level] || '‚ö™';

        alertText += `${levelEmoji} ALERTA ${index + 1}\n`;
        alertText += `Tipo: ${alert.type || 'General'}\n`;
        alertText += `Nivel: ${(alert.level || 'info').toUpperCase()}\n`;
        alertText += `Mensaje: ${alert.message}\n`;
        if (alert.action) {
            alertText += `Acci√≥n requerida: ${alert.action}\n`;
        }
        alertText += "\n";
    });

    alertText += `\nüìä RESUMEN DE ALERTAS:\n`;
    if (data.alert_counts) {
        Object.keys(data.alert_counts).forEach(level => {
            const count = data.alert_counts[level];
            if (count > 0) {
                alertText += `${level.toUpperCase()}: ${count}\n`;
            }
        });
    }
} else {
    alertText += "‚úÖ No hay alertas activas en el sistema.\n";
    alertText += "El sistema est√° funcionando dentro de par√°metros normales.\n";
}

alertText += `\n‚è∞ Actualizado: ${new Date(data.timestamp).toLocaleString()}`;

msg.payload = alertText;
return msg;

const data = msg.payload;
let recText = "üí° RECOMENDACIONES OPERATIVAS\n\n";

if (data.recommendations) {
    const categories = {
        'priority': 'üî• PRIORIDAD ALTA',
        'energy': '‚ö° ENERG√çA',
        'production': 'üè≠ PRODUCCI√ìN',
        'maintenance': 'üîß MANTENIMIENTO'
    };

    Object.keys(categories).forEach(category => {
        const recs = data.recommendations[category];
        if (recs && Array.isArray(recs) && recs.length > 0) {
            recText += `${categories[category]}:\n`;
            recs.forEach((rec, index) => {
                recText += `  ${index + 1}. ${rec}\n`;
            });
            recText += "\n";
        }
    });

    if (data.total_recommendations > 0) {
        recText += `üìä ESTAD√çSTICAS:\n`;
        recText += `Total de recomendaciones: ${data.total_recommendations}\n`;
        recText += `Recomendaciones prioritarias: ${data.priority_count}\n`;
    }
} else {
    recText += "‚ÑπÔ∏è No hay recomendaciones espec√≠ficas en este momento.\n";
    recText += "El sistema est√° operando de manera √≥ptima.\n";
}

recText += `\n‚è∞ Actualizado: ${new Date(data.timestamp).toLocaleString()}`;

msg.payload = recText;
return msg;
